package org.apromore.dao.model;

import org.apache.commons.lang.builder.EqualsBuilder;
import org.apache.commons.lang.builder.HashCodeBuilder;

import javax.persistence.Column;
import javax.persistence.Embeddable;
import java.io.Serializable;

/**
 * HeadVersionId generated by hbm2java
 */
@Embeddable
public class HeadVersionId implements Serializable {

    private Integer processId;
    private String version;


    /**
     * Default Constructor.
     */
    public HeadVersionId() { }

    public HeadVersionId(Integer processId, String version) {
       this.processId = processId;
       this.version = version;
    }


    @Column(name = "processId")
    public Integer getProcessId() {
        return this.processId;
    }

    public void setProcessId(Integer processId) {
        this.processId = processId;
    }


    @Column(name = "version", length = 20)
    public String getVersion() {
        return this.version;
    }

    public void setVersion(String version) {
        this.version = version;
    }



    /**
     * The equals standard method to test if the Head Version view is the same.
     * @param obj the other ID object
     * @return true if the same otherwise false
     */
    @Override
	public boolean equals(Object obj) {
        Boolean result = false;

        if (obj instanceof HeadVersionId) {
            HeadVersionId other = (HeadVersionId) obj;
            EqualsBuilder builder = new EqualsBuilder();
            builder.append(getProcessId(), other.getProcessId());
            builder.append(getVersion(), other.getVersion());
            result = builder.isEquals();
        }

        return result;
	}

    /**
     * Determines the hashcode of the object.
     * @return the hashcode
     */
    @Override
	public int hashCode() {
        HashCodeBuilder builder = new HashCodeBuilder();
        builder.append(getProcessId());
        builder.append(getVersion());
        return builder.toHashCode();
	}

}


